import React from 'react';
import './App.css';
import Color from './Color.js';
import "./Color.css";

class App extends React.Component {
  constructor(props) {
     super(props);
     this.state={col1:"",col2:""};
     this.colors = [];
     this.colorChange = this.colorChange.bind(this);
     this.colorAdded = this.colorAdded.bind(this);


}
  componentDidMount(){
    const fetchData = async () =>{

        const result = await fetch(`http://localhost:4000/color`);
        result.json().then(function(data) {
          console.log(data);
      });

    }

    fetchData();
    console.log(this.colors);


  }
  colorChange(e){

        this.setState({[e.target.name]:e.target.value});

  }

  colorAdded(e){


       this.colors.push({first:this.state.col1,second:this.state.col2});
       alert(this.colors);


  }

  render(){
     return(

       <div className="biggerPicture">
       <form onSubmit={this.colorAdded}>
       <label> First Color <input name="col1" type="text"  placeholder="Here..." onChange={this.colorChange} /> </label>
       <label> Secon Color <input name="col2" type="text"  placeholder="Here..." onChange={this.colorChange} /> </label>
       <input type="submit" value="Add it up" />
       </form>
       {this.colors.map(({first,second,index})=>(

            <Color key={first} color1={first} color2={second} />
        )
      )}
      </div>
      );

 }
}

export default App;
