import React from 'react';
import './App.css';
import Color from './Color.js';
import "./Color.css";

class App extends React.Component {
  constructor(props) {
     super(props);
     this.state={col1:"",col2:"",render:false};
     this.colors = [{first:"red",second:"yellow"}];
     this.colorChange = this.colorChange.bind(this);
     this.colorAdded = this.colorAdded.bind(this);




}
  async componentDidMount(){



        const result = await fetch(`http://localhost:4000/color`);
        const data = await result.json();

        data.map(({color1,color2})=>{

           this.colors.push({first:color1,second:color2});
        });
        this.setState({render:true});
}

  colorChange(e){

        this.setState({[e.target.name]:e.target.value});

  }

  async colorAdded(e){

       const colorJson = {
                            "color1":this.state.col1,
                            "color2":this.state.col2
       };

      fetch(`http://localhost:4000/newcolor`,{
         method:'POST',
         body: JSON.stringify(colorJson)}).then(function(response){
         alert(response);
       });



  }

  render(){
     return(

       <div className="biggerPicture">
       <form onSubmit={this.colorAdded}>
       <label> First Color <input name="col1" type="text"  placeholder="Here..." onChange={this.colorChange} /> </label>
       <label> Secon Color <input name="col2" type="text"  placeholder="Here..." onChange={this.colorChange} /> </label>
       <input type="submit" value="Add it up" />
       </form>
       {this.colors.map(({first,second,index})=>(
            <Color key={first} color1={first} color2={second} />
        )
      )}
      </div>
      );

 }
}

export default App;
