import React from 'react';
import './Color.css';

class Color extends React.Component {

  constructor(props){
    super(props);
    this.closed=this.closed.bind(this);
    this.state={color:"black",lock:"unlocked"};
    this.lock = this.lock.bind(this);

  }

  componentDidMount(){

    if(this.props.color2 == "black" ){

       this.setState({color:"white"});
    }

  }

  lock(){
   alert('hello');

  }

  async closed(){

    let colorJson= {
      "color1" :this.props.color1,
      "color2" :this.props.color2

    }

       fetch(`http://localhost:4000/deletecolor`,{
          method:'POST',
          headers: {
         'Content-Type': 'application/json'
     },
          body: JSON.stringify(colorJson)})
        setTimeout(()=>{console.log("Done");this.props.update();}, 150);
  }

  render(){
    return(<>

      <div className="each-color-composition">
         <div className="lock" onClick={this.lock}>{this.state.lock}</div>
         <div className="close" onClick={this.closed} style={{color:this.state.color}}> X </div>
        <div className="left-compose" style={{backgroundColor:this.props.color1}}>
        </div>
        <div className="right-compose" style={{backgroundColor:this.props.color2}}>
        </div>

      </div>
      </>
    )
  }


}


export default Color;
